// src/components/DiscoverPage.js

import React, { useState } from "react";
import FeaturedSlider from "./FeaturedSlider";
import MusicPlayerUI from "./MusicPlayerUI";
import {
  midnightMoodsPlaylist,
  partyStartersPlaylist,
  relaxingTonesPlaylist,
  // Import other playlists...
} from "../music/playlists";

const DiscoverPage = () => {
  const [currentPlaylist, setCurrentPlaylist] = useState(null);

  const handleListenNow = (playlistName) => {
    switch (playlistName) {
      case "Midnight Moods":
        setCurrentPlaylist(midnightMoodsPlaylist);
        break;
      case "Party Starters":
        setCurrentPlaylist(partyStartersPlaylist);
        break;
      case "Relaxing Tones":
        setCurrentPlaylist(relaxingTonesPlaylist);
        break;
      // Handle other cases...
      default:
        setCurrentPlaylist(null);
    }
  };

  return (
    <div style={{ padding: 20, color: "white" }}>
      <h1>Welcome to Music Information Management System ðŸŽ‰</h1>
      <FeaturedSlider onListenNow={handleListenNow} />
      {/* Other components like FeaturedArtists, RecommendedAlbums, etc. */}
      {currentPlaylist && <MusicPlayerUI playlist={currentPlaylist} />}
    </div>
  );
};

export default DiscoverPage;
