// src/components/MusicPlayerUI.js

import React, { useEffect, useRef, useState } from "react";
import "../styles/MusicPlayer.css";

const MusicPlayerUI = ({ playlist }) => {
  const audioRef = useRef(null);
  const progressRef = useRef(null);
  const [index, setIndex] = useState(0);
  const [rotating, setRotating] = useState(false);
  const [rotation, setRotation] = useState(0);
  const [intervalId, setIntervalId] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);

  const song = playlist[index];

  const playPause = () => {
    if (!audioRef.current) return;
    if (audioRef.current.paused) {
      audioRef.current.play();
      setIsPlaying(true);
      startRotation();
    } else {
      audioRef.current.pause();
      setIsPlaying(false);
      pauseRotation();
    }
  };

  const startRotation = () => {
    if (!rotating) {
      setRotating(true);
      const id = setInterval(() => {
        setRotation((r) => r + 1);
      }, 50);
      setIntervalId(id);
    }
  };

  const pauseRotation = () => {
    clearInterval(intervalId);
    setRotating(false);
  };

  const nextSong = () => {
    setIndex((prev) => (prev + 1) % playlist.length);
  };

  const prevSong = () => {
    setIndex((prev) => (prev - 1 + playlist.length) % playlist.length);
  };

  useEffect(() => {
    const audio = audioRef.current;
    if (audio) {
      const handleCanPlayThrough = () => {
        audio.play().then(() => {
          setIsPlaying(true);
          startRotation();
        }).catch(error => {
          console.error("Playback failed:", error);
        });
      };

      audio.addEventListener("canplaythrough", handleCanPlayThrough);
      audio.load();

      return () => {
        audio.removeEventListener("canplaythrough", handleCanPlayThrough);
      };
    }
  }, [index]);

  const handleProgressChange = (e) => {
    audioRef.current.currentTime = e.target.value;
  };

  return (
    <div className="music-player">
      <div className="album-cover">
        <img
          id="rotatingImage"
          src={song.cover}
          alt={song.title}
          style={{ transform: `rotate(${rotation}deg)` }}
        />
        <span className="point"></span>
      </div>

      <h2>{song.title}</h2>
      <p>{song.artist}</p>

      <audio
        ref={audioRef}
        onEnded={nextSong}
        onTimeUpdate={() => {
          if (progressRef.current && audioRef.current) {
            progressRef.current.value = audioRef.current.currentTime;
            progressRef.current.max = audioRef.current.duration;
          }
        }}
      >
        <source src={song.src} type="audio/mpeg" />
      </audio>

      <input
        type="range"
        defaultValue="0"
        ref={progressRef}
        onChange={handleProgressChange}
        id="progress"
      />

      <div className="controls">
        <button className="backward" onClick={prevSong}>
          <i className="fa-solid fa-backward"></i>
        </button>
        <button className="play-pause-btn" onClick={playPause}>
          <i
            className={`fa-solid ${isPlaying ? "fa-pause" : "fa-play"}`}
            id="controlIcon"
          ></i>
        </button>
        <button className="forward" onClick={nextSong}>
          <i className="fa-solid fa-forward"></i>
        </button>
      </div>
    </div>
  );
};

export default MusicPlayerUI;
