// MusicPlayerUI.js
import React, { useEffect, useRef, useState } from "react";
import "../styles/MusicPlayer.css";

const playlist = [
  {
    title: "Redemption",
    artist: "Besomorph & Coopex",
    src: "https://github.com/ecemgo/mini-samples-great-tricks/raw/main/song-list/Besomorph-Coopex-Redemption.mp3",
    cover:
      "https://github.com/ecemgo/mini-samples-great-tricks/assets/13468728/398875d0-9b9e-494a-8906-210aa3f777e0",
  },
  {
    title: "What's The Problem?",
    artist: "OSKI",
    src: "https://github.com/ecemgo/mini-samples-great-tricks/raw/main/song-list/OSKI-Whats-The-Problem.mp3",
    cover:
      "https://github.com/ecemgo/mini-samples-great-tricks/assets/13468728/810d1ddc-1168-4990-8d43-a0ffee21fb8c",
  },
  {
    title: "Control",
    artist: "Unknown Brain x Rival",
    src: "https://github.com/ecemgo/mini-samples-great-tricks/raw/main/song-list/Unknown-BrainxRival-Control.mp3",
    cover:
      "https://github.com/ecemgo/mini-samples-great-tricks/assets/13468728/7bd23b84-d9b0-4604-a7e3-872157a37b61",
  },
];

function MusicPlayerUI() {
  const audioRef = useRef(null);
  const progressRef = useRef(null);
  const [index, setIndex] = useState(0);
  const [rotating, setRotating] = useState(false);
  const [rotation, setRotation] = useState(0);
  const [intervalId, setIntervalId] = useState(null);

  const song = playlist[index];

  const playPause = () => {
    if (!audioRef.current) return;
    if (audioRef.current.paused) {
      audioRef.current.play();
      startRotation();
    } else {
      audioRef.current.pause();
      pauseRotation();
    }
  };

  const startRotation = () => {
    if (!rotating) {
      setRotating(true);
      const id = setInterval(() => {
        setRotation((r) => r + 1);
      }, 50);
      setIntervalId(id);
    }
  };

  const pauseRotation = () => {
    clearInterval(intervalId);
    setRotating(false);
  };

  const nextSong = () => {
    setIndex((prev) => (prev + 1) % playlist.length);
  };

  const prevSong = () => {
    setIndex((prev) => (prev - 1 + playlist.length) % playlist.length);
  };

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.load();
      audioRef.current.play();
      startRotation();
    }
  }, [index]);

  const handleProgressChange = (e) => {
    audioRef.current.currentTime = e.target.value;
  };

  return (
    <div className="music-player">
      <div className="album-cover">
        <img
          id="rotatingImage"
          src={song.cover}
          alt=""
          style={{ transform: `rotate(${rotation}deg)` }}
        />
        <span className="point"></span>
      </div>

      <h2>{song.title}</h2>
      <p>{song.artist}</p>

      <audio ref={audioRef} onEnded={nextSong} onTimeUpdate={() => {
        progressRef.current.value = audioRef.current.currentTime;
        progressRef.current.max = audioRef.current.duration;
      }}>
        <source src={song.src} type="audio/mpeg" />
      </audio>

      <input
        type="range"
        defaultValue="0"
        ref={progressRef}
        onChange={handleProgressChange}
        id="progress"
      />

      <div className="controls">
        <button className="backward" onClick={prevSong}>
          <i className="fa-solid fa-backward"></i>
        </button>
        <button className="play-pause-btn" onClick={playPause}>
          <i
            className={`fa-solid ${audioRef.current?.paused ? "fa-play" : "fa-pause"}`}
            id="controlIcon"
          ></i>
        </button>
        <button className="forward" onClick={nextSong}>
          <i className="fa-solid fa-forward"></i>
        </button>
      </div>
    </div>
  );
}

export default MusicPlayerUI;
