import React, { useState } from "react";
import axios from "axios";

function SearchByArtist() {
  // State variables for artist name input and search results
  const [artistName, setArtistName] = useState("");
  const [results, setResults] = useState([]);
  const [error, setError] = useState(""); // State for handling errors

  // Function to handle the search operation
  const handleSearch = async () => {
    if (!artistName) {
      setError("Artist name cannot be empty.");
      return;
    }
    setError(""); // Clear previous errors
    try {
      // Send a POST request to the backend with the artist name
      const response = await axios.post("http://localhost:8080/api/search-artist", {
        artistName: artistName,
      });
      // Update the results state with data from the backend
      setResults(response.data);
    } catch (error) {
      console.error("Error searching artist:", error);
      setError("Failed to fetch results. Please try again.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Search by Artist</h1>
      {/* Input field for artist name */}
      <input
        type="text"
        placeholder="Enter artist name"
        value={artistName}
        onChange={(e) => setArtistName(e.target.value)}
        style={{ padding: "10px", width: "300px", marginRight: "10px" }}
      />
      {/* Search button */}
      <button onClick={handleSearch} style={{ padding: "10px 20px" }}>
        Search
      </button>
      {/* Display error message, if any */}
      {error && <p style={{ color: "red", marginTop: "10px" }}>{error}</p>}
      {/* Display search results */}
      <div style={{ marginTop: "20px" }}>
        <h2>Results:</h2>
        {results.length > 0 ? (
          <ul>
            {results.map((result) => (
              <li key={result.id}>{result.title}</li>
            ))}
          </ul>
        ) : (
          <p>No results found.</p>
        )}
      </div>
    </div>
  );
}

export default SearchByArtist;
