import React, { useState } from "react";
import FeaturedSlider from "./FeaturedSlider";
import FeaturedArtists from "./FeaturedArtists";
import MusicPlayerUI from "./MusicPlayerUI";

const DiscoverPage = () => {
  const [selectedPlaylist, setSelectedPlaylist] = useState(null);
  const [showPlayer, setShowPlayer] = useState(false);

  const handleListenNow = (playlist) => {
    setSelectedPlaylist(playlist);
    setShowPlayer(true);
  };

  return (
    <div style={{ padding: 20, color: "white" }}>
      <h1>Welcome to Music Information Management System ðŸŽ‰</h1>
      <FeaturedSlider onListenNow={handleListenNow} />
      <FeaturedArtists />
      {showPlayer && selectedPlaylist && (
        <div style={{ position: "fixed", bottom: 0, width: "100%", zIndex: 1000 }}>
          <MusicPlayerUI playlist={selectedPlaylist} />
        </div>
      )}
    </div>
  );
};

export default DiscoverPage;
