import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import "./App.css"; // Import CSS
import SearchByArtist from "./components/SearchByArtist";
import SearchBySong from "./components/SearchBySong";
import SearchByLyrics from "./components/SearchByLyrics";
import AddArtist from "./components/AddArtist"; // Import AddArtist

function App() {
  return (
    <Router>
      <div className="app">
        <header className="app-header">
          <h1>Welcome to Music Information Management System</h1>
        </header>
        <main className="app-main">
          <Link to="/search-by-artist" className="button">
            Search by Artist
          </Link>
          <Link to="/search-by-song" className="button">
            Search By Song
          </Link>
          <Link to="/search-by-lyrics" className="button">
            Search By Lyrics
          </Link>
          <Link to="/add-artist" className="button">  {/* Add this button */}
            Add Artist
          </Link>
        </main>
        <footer className="app-footer">
          <p>&copy; 2024 Music Management System</p>
        </footer>

        <Routes>
          <Route path="/search-by-artist" element={<SearchByArtist />} />
          <Route path="/search-by-song" element={<SearchBySong />} />
          <Route path="/search-by-lyrics" element={<SearchByLyrics />} />
          <Route path="/add-artist" element={<AddArtist />} /> {/* Add this Route */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
