// src/components/PlayMusic.js
import React, { useEffect } from "react";
import { loadScript, loadStyle } from "../loadAssets"; // We'll define loadAssets below
import $ from "jquery"; // npm install jquery
import "../App.css"; // Place the jPlayer icon CSS in App.css (or a separate file)

function PlayMusic() {
  useEffect(() => {
    (async function initJPlayer() {
      try {
        // 1) Load jQuery UI (CSS + Script)
        await loadScript("/libs/jquery.min.js");
        await loadStyle("/libs/jquery-ui.css");
        await loadScript("/libs/jquery-ui.min.js");
        await loadScript("/libs/jquery.jplayer.min.js");
        await loadScript("/libs/jplayer.playlist.min.js");

        // 3) Initialize with your 3 local MP3s
        initPlayer();
      } catch (err) {
        console.error("Error loading jPlayer scripts:", err);
      }
    })();

    function initPlayer() {
      const playlist = [
        {
          title: "Song 01",
          artist: "Artist A",
          mp3: "/music/Rose_Ft_Bruno_Mars_-_APT.mp3"
        },
        {
          title: "Song 02",
          artist: "Artist B",
          mp3: "/music/Sabrina Carpenter  Nonsense Official Audio.mp3"
        },
        {
          title: "Song 03",
          artist: "Artist C",
          mp3: "/music/The Kid LAROI Justin Bieber STAY.mp3"
        }
      ];

      const cssSelector = {
        jPlayer: "#jquery_jplayer",
        cssSelectorAncestor: ".music-player"
      };

      const options = {
        swfPath: "https://cdnjs.cloudflare.com/ajax/libs/jplayer/2.9.2/jplayer/",
        supplied: "mp3",
        volume: 0.5
      };

      if (window.jPlayerPlaylist) {
        const myPlaylist = new window.jPlayerPlaylist(cssSelector, playlist, options);
      } else {
        console.error("jPlayerPlaylist not available. Check script loading.");
      }
    }
  }, []);

  return (
    <div className="music-player-page" style={{ textAlign: "center", marginTop: "40px" }}>
      <h2>Play Music (3 Local MP3s)</h2>

      <div className="music-player" style={{ margin: "0 auto" }}>
        {/* The main jPlayer container */}
        <div id="jquery_jplayer" className="jp-jplayer"></div>

        <div className="info">
          <div className="left">
            <a href="#!" className="icon-shuffle"></a>
            <a href="#!" className="icon-heart"></a>
          </div>
          <div className="center">
            <div className="jp-playlist">
              <ul>
                <li></li>
              </ul>
            </div>
          </div>
          <div className="right">
            <a href="#!" className="icon-repeat"></a>
            <a href="#!" className="icon-share"></a>
          </div>
        </div>

        <div className="controls">
          <div className="current jp-current-time">00:00</div>
          <div className="play-controls">
            <a href="#!" className="icon-previous jp-previous" title="previous"></a>
            <a href="#!" className="icon-play jp-play" title="play"></a>
            <a href="#!" className="icon-pause jp-pause" title="pause"></a>
            <a href="#!" className="icon-next jp-next" title="next"></a>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PlayMusic;
