// FeaturedSlider.js
import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Pagination, EffectCoverflow } from "swiper/modules";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/effect-coverflow";
import "../styles/Slider.css";

//images for local (import kk??)
import midnightMoods from '../images/MidnightMoods.jpg';
import Party from '../images/partystarters.jpg';
import relaxingtones from '../images/RelaxingTones.jpg';
import jazz from '../images/Jazz.jpg';
import Uplifting from '../images/Uplifting.jpg';

const slides = [
  {
    title: "Midnight Moods",
    image:
    midnightMoods,
  },
  {
    title: "Party Starters",
    image:
    Party,
  },
  {
    title: "Relaxing Tones",
    image:
    relaxingtones,
  },
  {
    title: "Smooth Jazz Journey",
    image:
      jazz,
  },
  {
    title: "Uplifting Rhythms",
    image:
    Uplifting,
  },
];

const FeaturedSlider = () => {
  return (
    <div className="slider-container">
      <h1>Popular Playlist</h1>
      <Swiper
        effect="coverflow"
        grabCursor={true}
        centeredSlides={true}
        loop={true}
        speed={600}
        slidesPerView="auto"
        coverflowEffect={{
          rotate: 10,
          stretch: 120,
          depth: 200,
          modifier: 1,
          slideShadows: false,
        }}
        pagination={{ el: ".swiper-pagination" }}
        modules={[EffectCoverflow, Pagination]}
        className="swiper"
      >
        {slides.map((slide, index) => (
          <SwiperSlide key={index} className="swiper-slide">
            <img src={slide.image} alt={slide.title} />
            <div className="slide-overlay">
              <h2>{slide.title}</h2>
              <button>
                Listen Now <i className="fa-solid fa-circle-play"></i>
              </button>
            </div>
          </SwiperSlide>
        ))}
        <div className="swiper-pagination"></div>
      </Swiper>
    </div>
  );
}

export default FeaturedSlider;
