import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Pagination, EffectCoverflow } from "swiper/modules";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/effect-coverflow";
import "../styles/Slider.css";

// Import images
import midnightMoods from '../images/MidnightMoods.jpg';
import party from '../images/partystarters.jpg';
import relaxing from '../images/RelaxingTones.jpg';
import jazz from '../images/Jazz.jpg';
import uplifting from '../images/Uplifting.jpg';

// Define playlists
const playlists = {
  midnightMoods: [
    {
      title: 'Midnight Song 1',
      artist: 'Artist A',
      src: '/music/midnight1.mp3',
      cover: '/images/midnight1.jpg',
    },
    // Add more songs
  ],
  partyStarters: [
    {
      title: 'Party Song 1',
      artist: 'Artist B',
      src: '/music/party1.mp3',
      cover: '/images/party1.jpg',
    },
    // Add more songs
  ],
  relaxingTones: [
    {
      title: 'Relaxing Song 1',
      artist: 'Artist C',
      src: '/music/relaxing1.mp3',
      cover: '/images/relaxing1.jpg',
    },
    // Add more songs
  ],
  smoothJazz: [
    {
      title: 'Jazz Song 1',
      artist: 'Artist D',
      src: '/music/jazz1.mp3',
      cover: '/images/jazz1.jpg',
    },
    // Add more songs
  ],
  upliftingRhythms: [
    {
      title: 'Uplifting Song 1',
      artist: 'Artist E',
      src: '/music/uplifting1.mp3',
      cover: '/images/uplifting1.jpg',
    },
    // Add more songs
  ],
};

const slides = [
  { title: 'Midnight Moods', image: midnightMoods, key: 'midnightMoods' },
  { title: 'Party Starters', image: party, key: 'partyStarters' },
  { title: 'Relaxing Tones', image: relaxing, key: 'relaxingTones' },
  { title: 'Smooth Jazz Journey', image: jazz, key: 'smoothJazz' },
  { title: 'Uplifting Rhythms', image: uplifting, key: 'upliftingRhythms' },
];

const FeaturedSlider = ({ onListenNow }) => {
  return (
    <div className="slider-container">
      <h1>Popular Playlist</h1>
      <Swiper
        effect="coverflow"
        grabCursor={true}
        centeredSlides={true}
        loop={true}
        speed={600}
        slidesPerView="auto"
        coverflowEffect={{
          rotate: 10,
          stretch: 120,
          depth: 200,
          modifier: 1,
          slideShadows: false,
        }}
        pagination={{ el: ".swiper-pagination" }}
        modules={[EffectCoverflow, Pagination]}
        className="swiper"
      >
        {slides.map((slide, index) => (
          <SwiperSlide key={index} className="swiper-slide">
            <img src={slide.image} alt={slide.title} />
            <div className="slide-overlay">
              <h2>{slide.title}</h2>
              <button onClick={() => onListenNow(playlists[slide.key])}>
                Listen Now <i className="fa-solid fa-circle-play"></i>
              </button>
            </div>
          </SwiperSlide>
        ))}
        <div className="swiper-pagination"></div>
      </Swiper>
    </div>
  );
};

export default FeaturedSlider;
