import React, { useState } from "react";
import "./Search.css"; // Import a CSS file for styling

function SearchBySong() {
  const [keyword, setKeyword] = useState("");
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState([]);

  const handleSearch = async () => {
    setLoading(true);
    setResults([]);
    try {
      const response = await fetch(
        `http://localhost:8080/api/songs/search?keyword=${encodeURIComponent(keyword)}`
      );
      const data = await response.json();
      setResults(data);
    } catch (error) {
      console.error("Error fetching songs:", error);
    }
    setLoading(false);
  };

  return (
    <div className="search-container">
      <h2>Search By Song</h2>
      <div className="search-box">
        <input
          type="text"
          placeholder="Enter song name"
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
          className="input-field"
        />
        <button onClick={handleSearch} className="button">Search</button>
      </div>
      {loading && <div className="loader">Loading...</div>}
      <ul className="results-list">
        {results.map((song, index) => (
          <li key={index} className="result-item">{song.title} - {song.artist}</li>
        ))}
      </ul>
    </div>
  );
}

export default SearchBySong;
