import React, { useState, useEffect } from "react";

function ViewArtists() {
  const [artists, setArtists] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchArtists();
  }, []);

  const fetchArtists = async () => {
    try {
      const response = await fetch("http://localhost:8080/api/fartists/all");
      if (response.ok) {
        const data = await response.json();
        setArtists(data);
      } else {
        console.error("Failed to fetch artists");
      }
    } catch (error) {
      console.error("Error fetching artists:", error);
    } finally {
      setLoading(false);
    }
  };

  const deleteArtist = async (artistName) => {
    if (!window.confirm(`Are you sure you want to delete "${artistName}"?`)) return;

    try {
      const response = await fetch(`http://localhost:8080/api/remove/artist?artistName=${encodeURIComponent(artistName)}`, {
        method: "DELETE",
      });
      if (response.ok) {
        setArtists(artists.filter(artist => artist.artist_name !== artistName));
        alert(`"${artistName}" deleted successfully.`);
      } else {
        alert(`Failed to delete "${artistName}".`);
      }
    } catch (error) {
      console.error("Error deleting artist:", error);
    }
  };

  return (
    <div className="view-artists-container">
      <h2>All Artists</h2>
      {loading ? <p>Loading artists...</p> : (
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Sort Name</th>
              <th>Disambiguation</th>
              <th>Type ID</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {artists.map((artist) => (
              <tr key={artist.artist_name}>
                <td>{artist.artist_name}</td>
                <td>{artist.artist_sort_name}</td>
                <td>{artist.artist_disambiguation}</td>
                <td>{artist.type_id}</td>
                <td>
                  <button onClick={() => deleteArtist(artist.artist_name)} className="delete-button">Remove</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ViewArtists;
